import React from 'react';
import heroImage from '../assets/hero.jpg';
import tiktokIcon from '../assets/tiktok.png';
import instagramIcon from '../assets/instagram.png';
import Button from './Button';

const Hero = () => {
  return (
    <div className="relative h-screen bg-cover bg-center flex flex-col justify-between" style={{ backgroundImage: `url(${heroImage})` }}>
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 flex flex-col items-center justify-center text-center text-white flex-grow">
        <Button to="/events">
          unplug
        </Button>
        <div className="mt-4 text-lg text-white" style={{ opacity: 0.85 }}>
          click for unplug events
        </div>
        <div className="flex space-x-4">
          <a href="https://tiktok.com" target="_blank" rel="noopener noreferrer">
            <img src={tiktokIcon} alt="TikTok" className="h-8 w-8" />
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src={instagramIcon} alt="Instagram" className="h-8 w-8" />
          </a>
        </div>
      </div>
      <p className="absolute w-4/5 text-white text-center font-light" style={{ opacity: 0.65, top: '65%', left: '50%', transform: 'translateX(-50%)', fontSize: 'clamp(1.5rem, 4vw, 10rem)' }}>
        In 2024, 20 million UK adults were on their screens for 11 hours a day
      </p>
    </div>
  );
};

export default Hero;
