/* eslint-disable react/button-has-type */
import React from 'react';

function Modal({ isOpen, onClose, email }) {
  const shareByEmail = () => {
    // Implement logic to share via email
    console.log('Sharing via email:', email);
  };

  const shareByPhone = () => {
    // Implement logic to share via phone number
    console.log('Sharing via phone:', email);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-500 bg-opacity-75">
      <div className="bg-[#FF9700] p-8 rounded-lg shadow-lg text-center w-full max-w-md">
        <h2 className="text-lg text-gray-700 mb-4">You have been added to The Unplug Club waitlist.</h2>
        <p className="text-lg text-gray-700 mb-4">Going with friends is always better, why not invite them?</p>
        <div className="flex justify-center space-x-4">
          <button onClick={shareByEmail} className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600">
            Share via Email
          </button>
          <button onClick={shareByPhone} className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600">
            Share via Phone
          </button>
        </div>
        <button onClick={onClose} className="mt-4 text-sm text-gray-500 hover:text-gray-700 focus:outline-none">
          Close
        </button>
      </div>
    </div>
  );
}

export default Modal;
