import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax

mapboxgl.accessToken = 'pk.eyJ1IjoidW5wbHVnamFjb2IiLCJhIjoiY2x4eW1hNjhoMDI5ZjJpcjJ6amY4bHY4aiJ9.bEQu354oGlWyzkpYpMsPrA'; // Replace with your Mapbox token

const MapComponent = ({ events }) => {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(-73.935242);
  const [lat, setLat] = useState(40.73061);
  const [zoom, setZoom] = useState(10);

  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/dark-v10',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });

    // Add markers to map
    events.forEach(event => {
      const marker = new mapboxgl.Marker()
        .setLngLat([event.longitude, event.latitude])
        .setPopup(new mapboxgl.Popup({ offset: 25 })
        .setHTML(`<h3>${event.name}</h3><p>${event.location}</p><p>${event.date} at ${event.time}</p>`))
        .addTo(map.current);
    });

  }, [events]); // rerun the effect if events change

  return (
    <div>
      <div ref={mapContainer} style={{ width: '100%', height: '400px' }} />
    </div>
  );
};

export default MapComponent;
