/* eslint-disable react/button-has-type */
/* eslint-disable react/no-unescaped-entities */
import React from 'react';
import instagramIcon from '../assets/instagram.png';
import whatsappIcon from '../assets/whatsapp.png';
import messageIcon from '../assets/message.png';

function Modal({ isOpen, onClose, email }) {
  const shareByEmail = () => {
    // Implement logic to share via email
    console.log('Sharing via email:', email);
  };

  const shareByPhone = () => {
    // Implement logic to share via phone number
    console.log('Sharing via phone:', email);
  };

  const shareViaInstagram = () => {
    // Implement logic to share via Instagram
    console.log('Sharing via Instagram:', email);
  };

  const shareViaWhatsApp = () => {
    // Implement logic to share via WhatsApp
    console.log('Sharing via WhatsApp:', email);
  };

  const shareViaMessage = () => {
    // Implement logic to share via Message
    console.log('Sharing via Message:', email);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-500 bg-opacity-75">
      <div className="bg-orange-500 border-4 border-white p-8 rounded-lg shadow-lg text-center w-full max-w-md">
        <h2 className="text-3xl font-bold mb-4 text-white">You've been added to The Unplug Club waitlist!</h2>
        <p className="text-lg font-semibold text-yellow-200 mb-4">Going with friends is always less scary. Invite them below!</p>
        <div className="flex justify-center space-x-4 mb-4">
          <button onClick={shareByEmail} className="bg-black border-2 border-black text-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            Share via Email
          </button>
          <button onClick={shareByPhone} className="bg-black border-2 border-black text-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            Share via Phone
          </button>
        </div>
        <div className="flex justify-center space-x-4">
          <button onClick={shareViaInstagram} className="bg-black border-2 border-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            <img src={instagramIcon} alt="Instagram" className="h-6 w-6 mr-2" />
            Instagram
          </button>
          <button onClick={shareViaWhatsApp} className="bg-white border-2 border-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            <img src={whatsappIcon} alt="WhatsApp" className="h-6 w-6 mr-2" />
            WhatsApp
          </button>
          <button onClick={shareViaMessage} className="bg-white border-2 border-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            <img src={messageIcon} alt="Message" className="h-6 w-6 mr-2" />
            Message
          </button>
        </div>
        <button onClick={onClose} className="mt-4 text-lg text-white hover:text-black focus:outline-none">
          Close
        </button>
      </div>
    </div>
  );
}

export default Modal;
