import React, { useState } from 'react';
import MapGL, { Marker, Popup } from 'react-map-gl';

const MAPBOX_TOKEN = 'pk.eyJ1IjoidW5wbHVnamFjb2IiLCJhIjoiY2x4eW1hNjhoMDI5ZjJpcjJ6amY4bHY4aiJ9.bEQu354oGlWyzkpYpMsPrA'; // Replace with your Mapbox token

const MapComponent = ({ events }) => {
  const [viewport, setViewport] = useState({
    width: '100%',
    height: 400,
    latitude: 37.7577,
    longitude: -122.4376,
    zoom: 8,
  });

  const [selectedEvent, setSelectedEvent] = useState(null);

  return (
    <MapGL
      {...viewport}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      mapboxApiAccessToken={MAPBOX_TOKEN}
      onViewportChange={nextViewport => setViewport(nextViewport)}
    >
      {events.map(event => (
        <Marker
          key={event.id}
          latitude={event.latitude}
          longitude={event.longitude}
        >
          <div onClick={() => setSelectedEvent(event)}>
            <img src="marker-icon.png" alt="Marker Icon" />
          </div>
        </Marker>
      ))}

      {selectedEvent && (
        <Popup
          latitude={selectedEvent.latitude}
          longitude={selectedEvent.longitude}
          onClose={() => setSelectedEvent(null)}
        >
          <div>
            <h2>{selectedEvent.name}</h2>
            <p>{selectedEvent.location}</p>
            <p>{selectedEvent.date} at {selectedEvent.time}</p>
          </div>
        </Popup>
      )}
    </MapGL>
  );
};

export default MapComponent;
