import React, { useState, useEffect } from 'react';
import SocialButtons from './SocialButtons';
import LogoButton from './LogoButton';
import heroImageDefault from '../assets/hero.jpg';
import heroImageHover from '../assets/unplug.jpg';

const Hero = () => {
  const [backgroundImage, setBackgroundImage] = useState(heroImageDefault);
  const [isGlitching, setIsGlitching] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [caption, setCaption] = useState("In 2024, 20 million UK adults were\n on their screens for 11 hours a day");
  const [hoverTransitionComplete, setHoverTransitionComplete] = useState(false);

  const handleMouseEnter = () => {
    setIsGlitching(true);
    setIsHovered(true);
    setTimeout(() => {
      setIsGlitching(false);
      setBackgroundImage(heroImageHover);
      setCaption("9 in 10 adults say they are happier\n after spending an hour a week offline");
      setHoverTransitionComplete(true);
    }, 400); // Duration of the glitch effect
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setHoverTransitionComplete(false);
    setBackgroundImage(heroImageDefault);
    setCaption("In 2024, 20 million UK adults were\n on their screens for 11 hours a day");
  };

  const overlayStyle = backgroundImage === heroImageHover
    ? { backgroundColor: 'rgba(255, 255, 255, 0.1)' }
    : { backgroundColor: 'rgba(0, 0, 0, 0.5)' };

  const textColor = hoverTransitionComplete ? 'text-black' : 'text-white';

  return (
    <div className={`hero-container relative h-screen bg-cover bg-center ${isGlitching ? 'color-glitch' : ''}`} style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="absolute inset-0" style={overlayStyle}></div>
      <div className={`relative z-10 flex flex-col items-center justify-center text-center flex-grow`}>
        <div className="flex flex-col items-center justify-center h-full">
          <div className={`mb-4 text-2xl transition-colors duration-300 ${textColor}`} style={{ opacity: 0.75 }}>
            click for unplug events
          </div>
          <LogoButton onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} />
          <SocialButtons isHovered={hoverTransitionComplete} />
        </div>
      </div>
      <p className="absolute w-4/5 text-white text-center font-light" style={{ opacity: 0.75, top: '70%', left: '50%', transform: 'translateX(-50%)', fontSize: 'clamp(1.5rem, 4vw, 10rem)' }}>
        {caption.split('\n').map((line, index) => (
          <React.Fragment key={index}>
            {line}
            <br />
          </React.Fragment>
        ))}
      </p>
    </div>
  );
};

export default Hero;
