import React, { useState } from 'react';
import { collection, addDoc } from 'firebase/firestore';
import { db } from '../firebase';

function WaitlistForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
  });
  const [submissionMessage, setSubmissionMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value.slice(0, 50), // Limit to 50 characters
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const docRef = await addDoc(collection(db, 'users'), formData);
      console.log('Document written with ID: ', docRef.id);
      setSubmissionMessage({
        type: 'success',
        content: 'You have been added to the waitlist!',
      });
      // Reset form after submission
      setFormData({ firstName: '', lastName: '', email: '' });
    } catch (e) {
      console.error('Error adding document: ', e);
      setSubmissionMessage({
        type: 'error',
        content: 'An error occurred. Please try again later.',
      });
    }
  };

  const handleCloseMessage = () => {
    setSubmissionMessage(null);
  };

  return (
    <form className="w-full max-w-lg" onSubmit={handleSubmit}>
      <div className="flex flex-wrap mb-4">
        <div className="w-1/2 pr-2">
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            placeholder="first name"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full text-black"
            maxLength={50} // Set max length to 50 characters
            required
          />
        </div>
        <div className="w-1/2 pl-2">
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            placeholder="last name"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full text-black"
            required
          />
        </div>
      </div>
      <div className="flex flex-wrap mb-4">
        <div className="w-2/3 pr-2">
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="email address"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full text-black"
            required
          />
        </div>
        <div className="w-1/3 pl-2">
          <button type="submit" className="p-3 bg-black border-2 border-white text-white rounded-lg w-full">join waitlist</button>
        </div>
      </div>
      {submissionMessage && (
        <div className={`fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50 ${submissionMessage.type === 'success' ? 'text-green-500' : 'text-red-500'}`}>
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md text-center">
            <p className="text-xl font-semibold mb-4">{submissionMessage.content}</p>
            {submissionMessage.type === 'success' && (
              <div className="flex justify-center">
                <button className="p-3 bg-black text-white rounded-lg mr-2">Share via Email</button>
                <button className="p-3 bg-black text-white rounded-lg">Share via Phone</button>
              </div>
            )}
            <button onClick={handleCloseMessage} className="mt-4 text-sm text-gray-500 underline cursor-pointer">Close</button>
          </div>
        </div>
      )}
    </form>
  );
}

export default WaitlistForm;
