import React, { useState } from 'react';
import Button from './Button';
import SocialButtons from './SocialButtons';
import heroImageDefault from '../assets/hero.jpg';
import heroImageHover from '../assets/unplug.jpg';

const Hero = () => {
  const [backgroundImage, setBackgroundImage] = useState(heroImageDefault);
  const [isGlitching, setIsGlitching] = useState(false);
  const [caption, setCaption] = useState("In 2024, 20 million UK adults\nwere on their screens for 11 hours a day");

  const handleMouseEnter = () => {
    setIsGlitching(true);
    setTimeout(() => {
      setIsGlitching(false);
      setBackgroundImage(heroImageHover);
      setCaption("9 in 10 adults say they are happier\n after spending an hour a week offline");
    }, 200); // Duration of the glitch effect
  };

  const handleMouseLeave = () => {
    setBackgroundImage(heroImageDefault);
    setCaption("In 2024, 20 million UK adults\nwere on their screens for 11 hours a day");
  };

  return (
    <div className={`relative h-screen bg-cover bg-center flex flex-col justify-between ${isGlitching ? 'color-glitch' : ''}`} style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 flex flex-col items-center justify-center text-center text-white flex-grow">
        <div className="flex flex-col items-center justify-center h-full">
          <Button to="/events" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            unplug
          </Button>
          <div className="mt-4 text-lg text-white" style={{ opacity: 0.85 }}>
            click for unplug events
          </div>
          <SocialButtons />
        </div>
      </div>
      <p className="absolute w-4/5 text-white text-center font-light" style={{ opacity: 0.75, top: '62%', left: '50%', transform: 'translateX(-50%)', fontSize: 'clamp(1.5rem, 4vw, 10rem)' }}>
        {caption.split('\n').map((line, index) => (
          <React.Fragment key={index}>
            {line}
            <br />
          </React.Fragment>
        ))}
      </p>
    </div>
  );
};

export default Hero;
