/* eslint-disable react/button-has-type */
/* eslint-disable react/no-unescaped-entities */
import React from 'react';

function Modal({ isOpen, onClose }) {
  const shareByEmail = () => {
    const subject = encodeURIComponent('Join The Unplug Club Waitlist');
    const body = encodeURIComponent(`Hey! Check out The Unplug Club: ${window.location.href}`);
    window.open(`mailto:?subject=${subject}&body=${body}`);
  };

  const shareByPhone = () => {
    const message = encodeURIComponent(`Hey! Check out The Unplug Club: ${window.location.href}`);
    window.open(`sms:?body=${message}`);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-500 bg-opacity-75">
      <div className="bg-orange-500 border-4 border-white p-8 rounded-lg shadow-lg text-center w-full max-w-md">
        <h2 className="text-3xl font-bold mb-4 text-white">You've been added to The Unplug Club waitlist!</h2>
        <p className="text-lg font-semibold text-yellow-200 mb-4">Going with friends is always less scary. Invite them below!</p>
        <div className="flex justify-center space-x-4">
          <button onClick={shareByEmail} className="bg-white border-2 border-black text-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            Share via Email
          </button>
          <button onClick={shareByPhone} className="bg-white border-2 border-black text-black px-4 py-2 rounded-lg hover:bg-yellow-200 hover:text-black focus:outline-none">
            Share via Phone
          </button>
        </div>
        <button onClick={onClose} className="mt-4 text-lg text-white hover:text-black focus:outline-none">
          Close
        </button>
      </div>
    </div>
  );
}

export default Modal;
