import React from 'react';
import { Link } from 'react-router-dom';
import heroImage from '../assets/hero.jpg';
import tiktokIcon from '../assets/tiktok.png';
import instagramIcon from '../assets/instagram.png';

const Hero = () => {
  return (
    <div className="relative h-screen bg-cover bg-center" style={{ backgroundImage: `url(${heroImage})` }}>
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
        <h1 className="text-5xl font-bold">unplug</h1>
        <Link to="/events" className="mt-4 px-8 py-3 bg-white text-black rounded-full text-lg">
          click for unplug events
        </Link>
        <div className="flex space-x-4 mt-4">
          <a href="https://tiktok.com" target="_blank" rel="noopener noreferrer">
            <img src={tiktokIcon} alt="TikTok" className="h-8 w-8" />
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src={instagramIcon} alt="Instagram" className="h-8 w-8" />
          </a>
        </div>
        <p className="mt-6 text-2xl font-light">
          In 2024, 20 million UK adults were on their screens for 11 hours a day
        </p>
      </div>
    </div>
  );
};

export default Hero;
