import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import SocialButtons from './SocialButtons';
import heroImageDefault from '../assets/hero.jpg';
import heroImageHover from '../assets/unplug.jpg';
import logo from '../assets/maximiseLogo.png';

const Hero = () => {
  const [backgroundImage, setBackgroundImage] = useState(heroImageDefault);
  const [isGlitching, setIsGlitching] = useState(false);
  const [caption, setCaption] = useState("In 2024, 20 million UK adults were\n on their screens for 11 hours a day");

  const handleMouseEnter = () => {
    setIsGlitching(true);
    setTimeout(() => {
      setIsGlitching(false);
      setBackgroundImage(heroImageHover);
      setCaption("9 in 10 adults say they are happier\n after spending an hour a week offline");
    }, 300); // Duration of the glitch effect
  };

  const handleMouseLeave = () => {
    setBackgroundImage(heroImageDefault);
    setCaption("In 2024, 20 million UK adults were \non their screens for 11 hours a day");
  };

  return (
    <div className={`relative h-screen bg-cover bg-center flex flex-col ${isGlitching ? 'color-glitch' : ''}`} style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <header className="z-10 bg-black p-4">
        <nav className="container mx-auto flex justify-center items-center">
          <div className="flex items-center space-x-8 text-white text-xl">
            <Link to="/our-story" className="nav-link">our story</Link>
            <Link to="/events" className="nav-link">events</Link>
          </div>
          <div className="flex justify-center mx-10 text-white text-2xl font-bold">
            Unplug
          </div>
          <div className="flex items-center space-x-8 text-white text-xl">
            <Link to="/how-to-detox" className="nav-link">how to detox</Link>
            <Link to="/contact" className="nav-link">contact</Link>
          </div>
        </nav>
      </header>
      <div className="flex flex-col flex-grow items-center justify-center text-center text-white relative z-10">
        <Link
          to="/events"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          className="transform transition-transform duration-300 ease-in-out hover:scale-105 bg-white rounded-full p-2"
          style={{ borderRadius: '10px', opacity: 0.85 }}
        >
          <img src={logo} alt="Unplug Club" className="h-24" />
        </Link>
        <div className="mt-4 text-lg text-white" style={{ opacity: 0.85 }}>
          click for unplug events
        </div>
        <SocialButtons />
      </div>
      <p className="absolute w-4/5 text-white text-center font-light" style={{ opacity: 0.75, top: '70%', left: '50%', transform: 'translateX(-50%)', fontSize: 'clamp(1.5rem, 4vw, 10rem)' }}>
        {caption.split('\n').map((line, index) => (
          <React.Fragment key={index}>
            {line}
            <br />
          </React.Fragment>
        ))}
      </p>
      <footer className="w-full bg-black text-white text-center p-4 absolute bottom-0">
        <p>&copy; 2024 The Unplug Club. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Hero;
