import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import EventsData from '../data/EventsData';  // eslint-disable-line import/no-webpack-loader-syntax

mapboxgl.accessToken = 'pk.eyJ1IjoidW5wbHVnamFjb2IiLCJhIjoiY2x4eW1hNjhoMDI5ZjJpcjJ6amY4bHY4aiJ9.bEQu354oGlWyzkpYpMsPrA';
const MapComponent = ({ selectedEvent }) => {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(-73.935242);
  const [lat, setLat] = useState(40.730610);
  const [zoom, setZoom] = useState(10);

  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/light-v10', // Use a light style which is close to black-and-white
      center: [lng, lat],
      zoom: zoom,
    });

    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });

    // Add markers to the map
    EventsData.features.forEach((feature) => {
      const { coordinates } = feature.geometry;
      const { name, location, date, time } = feature.properties;

      // Create a DOM element for the marker
      const el = document.createElement('div');
      el.className = 'marker';

      new mapboxgl.Marker(el)
        .setLngLat(coordinates)
        .setPopup(new mapboxgl.Popup({ offset: 25 }) // add popups
          .setHTML(`<h3>${name}</h3><p>${location}</p><p>${date} at ${time}</p>`))
        .addTo(map.current);
    });
  }, []);

  useEffect(() => {
    if (selectedEvent && map.current) {
      const { latitude, longitude, name, location, date, time } = selectedEvent;

      // Fly to the selected event location
      map.current.flyTo({
        center: [longitude, latitude],
        zoom: 14,
        essential: true, // this animation is considered essential with respect to prefers-reduced-motion
      });
    }
  }, [selectedEvent]);

  return (
    <div>
      <div ref={mapContainer} className="map-container" style={{ width: '100%', height: '400px' }} />
    </div>
  );
};

export default MapComponent;
