import React from 'react';
import { Link } from 'react-router-dom';

const Button = ({ to, children, onMouseEnter, onMouseLeave }) => {
  return (
    <Link 
      to={to} 
      className="bg-white text-black shadow-lg flex items-center justify-center transition-transform duration-300 ease-in-out transform hover:scale-105" 
      style={{
        backgroundColor: 'rgba(255, 255, 255, 0.75)',
        color: 'rgba(0, 0, 0, 0.85)',
        borderRadius: '10px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.5)',
        fontSize: 'clamp(1.5rem, 4vw, 10rem)',
        padding: '0.2rem 1rem' // Reduced vertical padding
      }}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      {children}
    </Link>
  );
};

export default Button;
