/* eslint-disable react/button-has-type */
/* eslint-disable react/no-unescaped-entities */
import React from 'react';
import instagramIcon from '../assets/instagram.png';
import whatsappIcon from '../assets/whatsapp.png';
import messageIcon from '../assets/message.png';

function Modal({ isOpen, onClose, email }) {
  const shareViaInstagram = () => {
    const url = 'https://www.instagram.com/';
    window.open(`https://www.instagram.com/sharer.php?u=${encodeURIComponent(url)}`, '_blank');
  };

  const shareViaWhatsApp = () => {
    const text = `Check out The Unplug Club: https://www.example.com`;
    window.open(`whatsapp://send?text=${encodeURIComponent(text)}`, '_blank');
  };

  const shareViaMessage = () => {
    const text = `Check out The Unplug Club: https://www.example.com`;
    window.open(`sms:?body=${encodeURIComponent(text)}`, '_blank');
  };

  if (!isOpen) return null;

  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-500 bg-opacity-75">
      <div className="bg-[#FF9700] border-4 border-[#FFD9A1] p-8 rounded-lg shadow-lg text-center w-full max-w-md">
        <h2 className="text-3xl font-bold mb-4 text-white">You've been added to The Unplug Club waitlist!</h2>
        <p className="text-lg font-semibold text-[#FFD9A1] mb-4">Going with friends is always less scary. Invite them below!</p>
        <div className="flex justify-center">
          <div className="mx-4">
            <button onClick={shareViaInstagram} className="bg-transparent rounded-full focus:outline-none">
              <img src={instagramIcon} alt="Instagram" className="h-12 w-12 cursor-pointer" />
            </button>
          </div>
          <div className="mx-4">
            <button onClick={shareViaWhatsApp} className="bg-transparent rounded-full focus:outline-none">
              <img src={whatsappIcon} alt="WhatsApp" className="h-12 w-12 cursor-pointer" />
            </button>
          </div>
          <div className="mx-4">
            <button onClick={shareViaMessage} className="bg-transparent rounded-full focus:outline-none">
              <img src={messageIcon} alt="Message" className="h-12 w-12 cursor-pointer" />
            </button>
          </div>
        </div>
        <button onClick={onClose} className="mt-4 text-lg font-semibold text-white hover:text-black focus:outline-none">
          Close
        </button>
      </div>
    </div>
  );
}

export default Modal;
