import React, { useState } from 'react';

const WaitlistForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Add form submission logic here (e.g., send data to an API)
    console.log('Form data submitted:', formData);

    try {
      // Call your backend API to handle Google Sheets integration
      const response = await axios.post('/api/submitToGoogleSheets', formData);
      console.log('Response from server:', response.data);

      // Optionally, reset form data after successful submission
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
      });
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };
  return (
    <form className="w-full max-w-lg" onSubmit={handleSubmit}>
      <div className="flex flex-wrap mb-4">
        <div className="w-1/2 pr-2">
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            placeholder="first name"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full"
            required
          />
        </div>
        <div className="w-1/2 pl-2">
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            placeholder="last name"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full"
            required
          />
        </div>
      </div>
      <div className="flex flex-wrap mb-4">
        <div className="w-2/3 pr-2">
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="email address"
            className="p-3 mb-3 border-2 border-black rounded-lg w-full"
            required
          />
        </div>
        <div className="w-1/3 pl-2">
          <button type="submit" className="p-3 bg-black border-2 border-white text-white rounded-lg w-full">join waitlist</button>
        </div>
      </div>
    </form>
  );
};

export default WaitlistForm;
